@Configuration
@RequiredArgsConstructor
public class TestConfig {

    private final DateUtils dateUtils;

    private final HibernateUtils hibernateUtils;

    public static final String DEFAULT_PROFILE_PIC = "data:image/jpeg;base64,";
    public static final String storageConfigurationGuide = "{\"sideBarHeader\":\"Storage Configuration\",\"mainBarHeader\":\"Storage Configuration Guide\",\"mainArea\":[{\"id\":\"section1\",\"title\":\"Email Configuration\",\"description\":\"Configure the email properties required for sending emails from the system. This includes setting up the email host, port, credentials, and additional properties to ensure proper email functionality.\",\"steps\":[\"Navigate to the Email section by clicking the 'Email' icon on the settings dashboard.\",\"Fill in the required fields:- **Email Host**: The SMTP server address (e.g., email-smtp.us-east-2.amazonaws.com).- **Email Port**: The port number (e.g., 587 for TLS or 465 for SSL).- **Username**: The username for your email provider.- **Password**: The password associated with the email username.\",\"Enter the 'Email From' address that will appear as the sender of the emails.\",\"Specify additional properties to fine-tune the SMTP settings. Common properties include:  - mail.transport.protocol: Set to 'smtp'.  - mail.smtp.auth: Set to 'true' to enable authentication.  - mail.smtp.starttls.enable: Set to 'true' to enable TLS.  - mail.debug: Set to 'true' for debugging purposes.\",\"Optionally, use the 'Export' button to save the configuration or 'Import' button to upload an existing configuration file.\",\"Click the 'Submit' button to save the configuration.\"]},{\"id\":\"section2\",\"title\":\"Mobile Configuration\",\"description\":\"Set up mobile messaging providers like Twilio or TextLocal to enable SMS or WhatsApp communication.\",\"steps\":[\"Navigate to the Mobile section by clicking the 'Mobile' icon on the settings dashboard.\",\"Select the mobile provider (e.g., Twilio, TextLocal) from the dropdown menu.\",\"Fill in the required fields:- **Account SID**: Enter your Account SID provided by the mobile provider.- **Auth Token**: Enter the authentication token.- **Mobile Number**: Provide the primary mobile number.- **WhatsApp Mobile Number**: Specify the WhatsApp-enabled mobile number.\",\"Optionally, add additional properties like custom parameters using the 'Additional Properties' section.\",\"Use the 'Export' button to save the configuration or the 'Import' button to upload an existing configuration.\",\"Click the 'Submit' button to save the configuration.\"]},{\"id\":\"section3\",\"title\":\"AI Configuration\",\"description\":\"Configure AI services like OpenAI or Azure AI to integrate machine learning capabilities.\",\"steps\":[\"Navigate to the AI section by clicking the 'AI' icon on the settings dashboard.\",\"Select the AI provider (e.g., OpenAI, Azure AI) from the dropdown menu.\",\"For OpenAI:- **AI Key**: Enter your API key.- **Model**: Specify the model name (e.g., gpt-4.0).- **Version**: Provide the version if applicable.\",\"For Azure AI:- **Endpoint**: Enter the Azure AI endpoint.- **Model**: Specify the model name.- **Version**: Provide the version.- **Deployment Name**: Enter the deployment name for Azure AI.\",\"Use the 'Export' button to save the configuration or the 'Import' button to upload an existing configuration.\",\"Click the 'Submit' button to save the configuration.\"]},{\"id\":\"section4\",\"title\":\"Coupons Management\",\"description\":\"Manage discount coupons by adding, editing, or filtering coupon codes.\",\"steps\":[\"Navigate to the Coupons section by clicking the 'Coupons' icon on the settings dashboard.\",\"To add a coupon:- Click the 'Add Coupon' button.- Provide details such as the coupon code, discount percentage, and status.- Click 'Save' to add the coupon.\",\"To edit a coupon:- Click on a coupon in the table.- Modify details such as the discount percentage and status in the popup.- Click 'Save' to update the coupon.\",\"Use the filter box to search for specific coupon codes.\",\"The table displays details such as Coupon Code, Discount Percentage, Created Date, and Created By.\"]},{\"id\":\"section5\",\"title\":\"Keystore Configuration\",\"description\":\"Configure and manage keystore properties for secure communication and certificate linking.\",\"steps\":[\"Navigate to the Keystore section by clicking the 'Keystore' icon on the settings dashboard.\",\"Fill in the required fields:- **Algorithm**: Select the algorithm (e.g., RSA).- **Alias Name**: Enter a unique alias name.- **Key Size**: Specify the key size (e.g., 2048).- **Validity (Days)**: Enter the validity period in days.- **Keystore Password**: Provide a secure password for the keystore.- **Common Name (CN)**: Specify the common name.- **Organization Unit (OU)**: Enter the organizational unit.- **Organization (O)**: Provide the organization name.- **Locality (L)**: Specify the locality.- **State (S)**: Enter the state.- **Country Code (C)**: Provide the country code.\",\"Optionally, add additional properties in the 'Additional Properties' section.\",\"Click the 'Generate' button to create the keystore.\",\"To link a certificate:- Click 'Link Cert' in the Certificate Linking popup.- Download the linked certificate if required.\"]},{\"id\":\"section6\",\"title\":\"Proxy Configuration\",\"description\":\"Configure and manage proxy settings to control and secure outbound API requests through a proxy server.\",\"steps\":[\"Navigate to the Proxy Configuration section by clicking the 'Proxy' icon on the settings dashboard.\",\"Enable or disable proxy usage by toggling the 'Enable Proxy' option. When enabled, all outbound API requests will be routed through the configured proxy server for enhanced security and monitoring.\",\"Fill in the required fields for HTTP Proxy configuration:- **HTTP Host**: Specify the hostname or IP address of the HTTP proxy server.- **HTTP Port**: Enter the port number of the HTTP proxy server.- **HTTP Username**: Provide the username for authentication with the proxy server (if applicable).- **HTTP Password**: Provide the password for authentication with the proxy server (if applicable).\",\"For HTTPS Proxy configuration, you can select the 'Use same as HTTP' option or provide separate details for HTTPS Host, Port, Username, and Password.\",\"Understand the benefits of using a proxy:- **Security**: Adds an additional layer of protection by masking the server's IP address and filtering traffic.- **Monitoring**: Allows administrators to monitor, log, and control outbound requests effectively.- **Compliance**: Helps enforce company policies for internet usage and data access.\",\"Optionally, configure advanced settings, such as bypassing the proxy for specific URLs or IP ranges in the 'Advanced Configuration' section.\",\"Test the proxy configuration by saving the settings. The system will automatically validate the connection to the proxy server before completing the save. If any error occurs during the validation, an error message will be displayed, and the settings will not be saved until the issue is resolved.\",\"Ensure that the proxy server is reachable from the application server. If the proxy server is down or unreachable, all outbound API requests will fail. This setup provides administrators the ability to turn off or force-stop internet access on the server by disabling the proxy or making it unreachable.\",\"Click the 'Save' button to apply the proxy configuration. Changes will take effect immediately for all outbound API requests.\",\"To disable proxy usage, simply toggle off the 'Enable Proxy' option. This will revert to direct API communication without routing through the proxy server.\"]},{\"id\":\"section7\",\"title\":\"Storage Configuration\",\"description\":\"Configure storage settings for AWS S3, Google Cloud Storage, Azure ADLS, or Server Local Storage.\",\"steps\":[\"Navigate to the Storage section by clicking the 'Storage' icon on the settings dashboard.\",\"Select the storage provider (e.g., AWS S3, Google Cloud Storage, Azure ADLS, or Server Local Storage) from the dropdown menu.\",\"For AWS S3:- **AWS Access Key**: Enter the access key.- **AWS Secret Key**: Enter the secret key.- **AWS Bucket Name**: Provide the S3 bucket name.- **AWS Region**: Specify the AWS region.\",\"For Google Cloud Storage:- **Project ID**: Enter the project ID.- **Bucket Name**: Provide the bucket name.- **Credentials File**: Upload the credentials JSON file.\",\"For Azure ADLS:- **Account Endpoint**: Provide the Azure Blob Storage endpoint.- **Account Name**: Enter the account name.- **Account Key/SAS Token**: Provide the key or token.- **Container Name**: Specify the container name.\",\"For Server Local Storage:- **File Location**: Provide the file location (e.g., c:/admin/files/).\",\"Optionally, add additional properties using the 'Additional Properties' section.\",\"Use the 'Export' button to save the configuration or the 'Import' button to upload an existing configuration.\",\"Click the 'Submit' button to save the configuration.\"]}]}";
    public static final String troubleshootingLoginIssues = "{\"sideBarHeader\":\"Login Troubleshoot\",\"mainBarHeader\":\"Troubleshooting Login Issues\",\"mainArea\":[{\"id\":\"section1\",\"title\":\"Check Internet Connection\",\"description\":\"Ensure you have a stable internet connection. Test by opening other websites to confirm connectivity.\",\"steps\":[\"Restart your router or modem.\",\"Check your device's network settings.\",\"Contact your Internet Service Provider if the issue persists.\"]},{\"id\":\"section2\",\"title\":\"Verify Credentials\",\"description\":\"Ensure your username and password are correct. If you’ve forgotten your password, click 'Forgot password?' to reset it.\",\"steps\":[\"Double-check your username or email address.\",\"Reset your password using the 'Forgot password?' link.\",\"Contact support if your credentials are locked.\"]},{\"id\":\"section3\",\"title\":\"Enable Location Services\",\"description\":\"Location services may be required to verify your login. Follow these steps to enable location:\",\"steps\":[\"On Chrome (Windows/Mac): Go to Settings > Privacy and Security > Site Settings > Location. Enable 'Ask before accessing'.\",\"On Firefox (Windows/Mac): Go to Preferences > Privacy & Security > Permissions > Location. Enable location access for your site.\",\"On Safari (Mac/iPhone): Go to Preferences > Websites > Location. Select 'Allow' for your website.\",\"On Android: Open Settings > Location. Ensure location services are turned on.\",\"On iOS: Go to Settings > Privacy > Location Services. Enable location services and set your browser to 'While Using the App.'\"]},{\"id\":\"section4\",\"title\":\"Clear Browser Cache\",\"description\":\"Clearing the cache can resolve login issues. Check your browser settings to clear cache and cookies.\",\"steps\":[\"On Chrome: Go to Settings > Privacy and Security > Clear Browsing Data.\",\"On Firefox: Go to Options > Privacy & Security > Cookies and Site Data > Clear Data.\",\"On Safari: Go to Preferences > Privacy > Manage Website Data > Remove All.\"]},{\"id\":\"section5\",\"title\":\"Browser Compatibility\",\"description\":\"Ensure you are using a supported browser (e.g., Chrome, Firefox, Edge). Update your browser to the latest version if needed.\",\"steps\":[\"Use a modern browser like Chrome, Firefox, or Edge.\",\"Update your browser to the latest version.\",\"Disable outdated plugins or extensions that may interfere.\"]},{\"id\":\"section6\",\"title\":\"Disable Extensions\",\"description\":\"Browser extensions may interfere with the login process. Disable them temporarily and try again.\",\"steps\":[\"Open your browser's extensions or add-ons page.\",\"Disable any extensions related to ad-blocking or script blocking.\",\"Restart your browser and try logging in again.\"]},{\"id\":\"section7\",\"title\":\"Ensure Your Account is Active\",\"description\":\"If your account is deactivated, contact your administrator to reactivate it.\",\"steps\":[\"Verify your account status in your profile settings.\",\"Contact your administrator for reactivation if needed.\"]},{\"id\":\"section8\",\"title\":\"Contact Support\",\"description\":\"If the above steps don’t resolve the issue, contact our support team.\",\"steps\":[\"Email our support team at support@example.com.\",\"Provide details about the issue you are facing.\",\"Wait for a response within 24-48 hours.\"]}]}";
    public static final String STORAGE_CONF_GUIDE = "Storage Configuration Guide";
    public static final String TROUBLESHOOT_LOGIN_ISSUES = "Troubleshooting Login Issues";

    public static final String ADMIN = "admin";

    @PostConstruct
    public void init() {
        UserEntity admin = getUserEntity(ADMIN);
        if (admin == null) {
            admin = new UserEntity();
        }
        admin.setUserName(ADMIN);
        admin.setPassword(ADMIN);
        admin.setProfilePic(DEFAULT_PROFILE_PIC);
        admin.setDisplayName("Administrator");
        admin.setAdmin(true);
        hibernateUtils.saveOrUpdateEntity(admin);

        if (getFeature(CommonConstants.Features.EMAIL_SETTINGS) == null)
            hibernateUtils.saveOrUpdateEntity(new FeatureEntity(CommonConstants.Features.EMAIL_SETTINGS, dateUtils.getCurrentDate()));
        if (getFeature(CommonConstants.Features.MOBILE_SETTINGS) == null)
            hibernateUtils.saveOrUpdateEntity(new FeatureEntity(CommonConstants.Features.MOBILE_SETTINGS, dateUtils.getCurrentDate()));
        if (getFeature(CommonConstants.Features.AI_SETTINGS) == null)
            hibernateUtils.saveOrUpdateEntity(new FeatureEntity(CommonConstants.Features.AI_SETTINGS, dateUtils.getCurrentDate()));
        if (getFeature(CommonConstants.Features.COUPON_SETTINGS) == null)
            hibernateUtils.saveOrUpdateEntity(new FeatureEntity(CommonConstants.Features.COUPON_SETTINGS, dateUtils.getCurrentDate()));
        if (getFeature(CommonConstants.Features.PAYSLIP_SETTINGS) == null)
            hibernateUtils.saveOrUpdateEntity(new FeatureEntity(CommonConstants.Features.PAYSLIP_SETTINGS, dateUtils.getCurrentDate()));
        if (getFeature(CommonConstants.Features.KEYSTORE_SETTINGS) == null)
            hibernateUtils.saveOrUpdateEntity(new FeatureEntity(CommonConstants.Features.KEYSTORE_SETTINGS, dateUtils.getCurrentDate()));
        if (getFeature(CommonConstants.Features.STORAGE_SETTINGS) == null)
            hibernateUtils.saveOrUpdateEntity(new FeatureEntity(CommonConstants.Features.STORAGE_SETTINGS, dateUtils.getCurrentDate()));
        if (getFeature(CommonConstants.Features.VAULT_SETTINGS) == null)
            hibernateUtils.saveOrUpdateEntity(new FeatureEntity(CommonConstants.Features.VAULT_SETTINGS, dateUtils.getCurrentDate()));
        if (getFeature(CommonConstants.Features.PROXY_SETTINGS) == null)
            hibernateUtils.saveOrUpdateEntity(new FeatureEntity(CommonConstants.Features.PROXY_SETTINGS, dateUtils.getCurrentDate()));
        if (getFeature(CommonConstants.Features.LOGS) == null)
            hibernateUtils.saveOrUpdateEntity(new FeatureEntity(CommonConstants.Features.LOGS, dateUtils.getCurrentDate()));

        loadInitHelp();
    }

    private FeatureEntity getFeature(String featureName) {
        try (Session session = hibernateUtils.getSession()) {
            return (FeatureEntity) session.createQuery("FROM FeatureEntity WHERE featureName=:featureName").setParameter("featureName", featureName).uniqueResult();
        }
    }

    private UserEntity getUserEntity(String userName) {
        try (Session session = hibernateUtils.getSession()) {
            return (UserEntity) session.createQuery("FROM UserEntity WHERE userName=:userName").setParameter("userName", userName).uniqueResult();
        }
    }

    public void loadInitHelp() {
        HelpEntity storageConf = hibernateUtils.findEntityByCriteria(HelpEntity.class, "helpConfigName", STORAGE_CONF_GUIDE);
        HelpEntity troubleShootLogin = hibernateUtils.findEntityByCriteria(HelpEntity.class, "helpConfigName", TROUBLESHOOT_LOGIN_ISSUES);

        if (storageConf == null) {
            createHelpEntity(STORAGE_CONF_GUIDE, storageConfigurationGuide);
        }

        if (troubleShootLogin == null) {
            createHelpEntity(TROUBLESHOOT_LOGIN_ISSUES, troubleshootingLoginIssues);
        }
    }

    private void createHelpEntity(String title, String configuration) {
        Date date = dateUtils.getCurrentDate();
        HelpEntity helpEntity = new HelpEntity();
        helpEntity.setCreatedDate(date);
        helpEntity.setUpdatedDate(date);
        helpEntity.setHelpConfigName(title);
        helpEntity.setHelpConfiguration(configuration);
        hibernateUtils.saveOrUpdateEntity(helpEntity);
    }

}
